  const setToZero = () => setCounter(0);

   return (
     <div>
       <Display/>
       <button onClick={increaseByOne}>
         plus
       </button>
       <button onClick={setToZero}>
          zero
        </button>
     </div>
   )
}

const Display = (props) => {
  return (
    <div>{props.counter}</div>
  )
}



